export function specialCharacterValidator() {
  return control => {
    const value = control.value;
    if (value && /[!@#$%^&*(),.?":{}|<>]/.test(value)) {
      return null;
    } else {
      return {
        specialCharacterError: 'Password must contain one or more special character.'
      };
    }
  };
}
